open! Core
open! Import

let map_function_roots ~f functions =
  Map.map ~f:(Function.map_root ~f) functions
;;

let test_cfg s =
  s
  |> Parser.parse_string
  |> Result.map ~f:(map_function_roots ~f:Cfg.process)
  |> function
  | Error e -> Nod_error.to_string e |> print_endline
  | Ok fns ->
    Map.iter
      fns
      ~f:(fun { Function.root = ~root:_, ~blocks:_, ~in_order:blocks; _ } ->
        Vec.iter blocks ~f:(fun block ->
          let instrs =
            Vec.to_list block.Block.instructions @ [ block.terminal ]
          in
          print_s [%message block.id_hum (instrs : Ir.t list)]))
;;

let test_ssa ?don't_opt s =
  test_cfg s;
  print_endline "=================================";
  Parser.parse_string s
  |> Result.map ~f:(map_function_roots ~f:Cfg.process)
  |> Result.map ~f:Eir.set_entry_block_args
  |> Result.map ~f:(map_function_roots ~f:Ssa.create)
  |> function
  | Error e -> Nod_error.to_string e |> print_endline
  | Ok fns ->
    let go fns =
      Map.iter fns ~f:(fun { Function.root = (ssa : Ssa.t); _ } ->
        Vec.iter ssa.in_order ~f:(fun block ->
          let instrs = Vec.to_list block.instructions @ [ block.terminal ] in
          print_s
            [%message
              block.id_hum ~args:(block.args : Var.t Vec.t) (instrs : Ir.t list)]))
    in
    go fns;
    (match don't_opt with
     | Some () -> ()
     | None ->
       print_endline "******************************";
       Eir.optimize fns;
       go fns)
;;

let compile_and_lower ?(opt_flags = Eir.Opt_flags.no_opt) program =
  match Nod.compile ~opt_flags program with
  | Error e -> Nod_error.to_string e |> print_endline
  | Ok functions ->
    let asm = X86_backend.compile_to_asm functions in
    print_endline asm
;;

let borked = Examples.Textual.f

let%expect_test "borked" =
  compile_and_lower ~opt_flags:Eir.Opt_flags.no_opt borked;
  [%expect
    {|
    .intel_syntax noprefix
    .text
    .globl root
    root:
      push rbp
      push r12
      push r13
      push r14
      push r15
      mov rbp, rsp
      add rbp, 40
    root__start:
      mov r14, 7
      mov r13, 0
      mov r15, 0
    root__intermediate_start_to_outercheck:
      jmp root__outercheck
    root__intermediate_start_to_exit:
      mov r14, r11
      mov r14, r12
      jmp root__exit
    root__outercheck:
      mov r10, r13
      sub r10, r14
      cmp r10, 0
      je root__intermediate_outercheck_to_exit
    root__intermediate_outercheck_to_outerbody:
      jmp root__outerbody
    root__intermediate_outercheck_to_exit:
      mov r14, r11
      mov r14, r12
    root__exit:
      mov rax, r15
    root__root__epilogue:
      mov rsp, rbp
      sub rsp, 40
      pop r15
      pop r14
      pop r13
      pop r12
      pop rbp
      ret
    root__outerbody:
      mov r11, 0
      mov r12, 0
    root__intermediate_outerbody_to_innercheck:
      mov r10, r11
      mov r9, r12
      jmp root__innercheck
    root__intermediate_outerbody_to_outerinc:
      mov r10, r15
      jmp root__outerinc
    root__innercheck:
      mov r8, r10
      sub r8, 3
      cmp r8, 0
      je root__intermediate_innercheck_to_innerexit
    root__intermediate_innercheck_to_innerbody:
      jmp root__innerbody
    root__intermediate_innercheck_to_innerexit:
      mov r8, r9
      jmp root__innerexit
    root__outerinc:
      add r13, 1
      jmp root__outercheck
    root__innerbody:
      mov r8, r10
      and r8, 1
      sub r8, 0
      cmp r8, 0
      je root__intermediate_innerbody_to_skipeven
    root__intermediate_innerbody_to_dowork:
      jmp root__dowork
    root__intermediate_innerbody_to_skipeven:
      jmp root__skipeven
    root__innerexit:
      mov r9, r15
      add r9, r8
    root__intermediate_innerexit_to_outerinc:
      mov r10, r9
      jmp root__outerinc
    root__intermediate_innerexit_to_exit:
      mov r14, r10
      mov r14, r8
      mov r15, r9
      jmp root__exit
    root__dowork:
      mov rax, r13
      imul r10
      mov r8, rax
      add r9, r8
      add r10, 1
      jmp root__intermediate_dowork_to_innercheck
    root__skipeven:
      add r10, 1
      jmp root__intermediate_skipeven_to_innercheck
    root__intermediate_dowork_to_innercheck:
      jmp root__innercheck
    root__intermediate_dowork_to_innerexit:
      mov r8, r9
      jmp root__innerexit
    root__intermediate_skipeven_to_innercheck:
      jmp root__innercheck
    root__intermediate_skipeven_to_innerexit:
      mov r8, r9
      jmp root__innerexit
    .section .note.GNU-stack,"",@progbits
    |}]
